def part_one(input_string):
    """
    >>> part_one("(())")
    0
    >>> part_one("()()")
    0
    >>> part_one("(((") 
    3
    >>> part_one("(()(()(") 
    3
    >>> part_one("))(((((")
    3
    >>> part_one("())")
    -1
    >>> part_one("))(")
    -1
    >>> part_one(")))")
    -3
    >>> part_one(")())())")
    -3
    """

    floor = 0
    for direction in input_string:
        floor += 1 if direction == "(" else -1
    return floor

def part_two(in_s):
    """
    >>> part_two(")")
    1

    >>> part_two("()())")
    5
    """
    floor = 0
    for i, direction in enumerate(in_s):
        floor += 1 if direction == "(" else -1
        if floor < 0:  
            return i+1

